/* Minimal Bootstrap fallback JavaScript for CDN failure */
console.warn('Using Bootstrap fallback - limited functionality');
// Basic modal functionality fallback
window.bootstrap = {
  Modal: function(element) {
    return {
      show: function() { 
        if(element.style) element.style.display = 'block'; 
      },
      hide: function() { 
        if(element.style) element.style.display = 'none'; 
      }
    };
  },
  Tooltip: function() { return {show:function(){},hide:function(){}}; },
  Popover: function() { return {show:function(){},hide:function(){}}; }
};