/* Minimal Chart.js fallback for CDN failure */
console.warn('Chart.js CDN failed - using fallback with limited functionality');
window.Chart = function(ctx, config) {
  console.warn('Chart rendering disabled - CDN fallback active');
  return {
    update: function() {},
    destroy: function() {},
    data: config.data || {},
    options: config.options || {}
  };
};